// home.page.ts
import { Component } from '@angular/core';
import { AuthService } from '../service/auth.service';
import { HttpErrorResponse } from '@angular/common/http';
import { NavController } from '@ionic/angular';

@Component({
  selector: 'app-home',
  templateUrl: 'home.page.html',
  styleUrls: ['home.page.scss'],
})
export class HomePage {
  username: string = '';
  password: string = '';

  constructor(
    private authService: AuthService,
    private navCtrl: NavController
  ) {}

  login(username: string, password: string): void {
    this.authService.login(username, password).subscribe(
      (response: any) => {
        console.log('Login exitoso', response);
        // Redirigir a la página principal o realizar alguna acción adicional
      },
      (error: HttpErrorResponse) => {
        console.error('Error de autenticación', error);
      }
    );
  }

  irARegistro(): void {
    this.navCtrl.navigateForward('/registro');
  }

  irARecuperarContrasena(): void {
    this.navCtrl.navigateForward('/recuperar-contrasena');
  }
}
<ion-header [translucent]="true">
  <ion-toolbar>
    <ion-title>
      Iniciar Sesión
    </ion-title>
  </ion-toolbar>
</ion-header>

<ion-content [fullscreen]="true" class="ion-padding">
  <div class="ion-text-center">
    <h1>Bienvenido</h1>
  </div>

  <!-- Formulario de inicio de sesión -->
  <ion-item>
    <ion-label position="floating">Nombre de Usuario</ion-label>
    <ion-input [(ngModel)]="username" type="text"></ion-input>
  </ion-item>

  <ion-item>
    <ion-label position="floating">Contraseña</ion-label>
    <ion-input [(ngModel)]="password" type="password"></ion-input>
  </ion-item>

  <ion-button expand="full" (click)="login(username, password)">Ingresar</ion-button>

  <div class="ion-text-center">
    <!-- Enlace para ir a la página de registro -->
    <!-- home.page.html -->
    <ion-button (click)="irARecuperarContrasena()" fill="clear">Recuperar Contraseña</ion-button>
    <!-- Enlace para ir a la página de recuperación de contraseña -->
    <!-- home.page.html -->
    <ion-button (click)="irARecuperarContrasena()" fill="clear">Recuperar Contraseña</ion-button>

  </div>
</ion-content>
