import { Component, OnInit } from '@angular/core';
import { RegionService } from '../service/region.service';
import { Preferences } from '@capacitor/preferences'; // Importamos Capacitor Preferences
import { Region } from '../models/region.model';

@Component({
  selector: 'app-user-registration', // Asegúrate de que el selector sea correcto
  templateUrl: './user-registration.page.html',
  styleUrls: ['./user-registration.page.scss'],
})

export class UserRegistrationPage implements OnInit {
  regiones: Region[] = [];
  regionSeleccionada: Region; // Cambié el tipo de dato para representar la región seleccionada

  constructor(private regionService: RegionService) {}

  ngOnInit() {
    this.regionService.getRegiones().subscribe(response => {
      this.regiones = response.regiones;
      console.log(this.regiones);
    });
  }

  async guardarDatosEnPreferences() {
    try {
      // Asumo que `regionSeleccionada` contiene un objeto completo de la región seleccionada
      // y puedes almacenar más detalles según tus necesidades.
      const datosUsuario = {
        key: 'usuario_completo',
        value: JSON.stringify({
          region: this.regionSeleccionada,
          // Otros datos del usuario aquí
        }),
      };

      await Preferences.set(datosUsuario);

      console.log('Datos del usuario almacenados en Preferences correctamente');
    } catch (error) {
      console.error('Error al almacenar datos del usuario en Preferences:', error);
    }
  }
}
<ion-header [translucent]="true">
  <ion-toolbar>
    <ion-title>Registro de Usuario</ion-title>
  </ion-toolbar>
</ion-header>

<ion-content [fullscreen]="true" class="ion-padding">
  <!-- Input con lista desplegable -->
  <ion-item>
    <ion-label>Seleccione región</ion-label>
    <ion-select placeholder="Seleccionar región" [(ngModel)]="regionSeleccionada">
      <!-- Iterar sobre las regiones y mostrarlas en la lista -->
      <ion-select-option *ngFor="let region of regiones" [value]="region">
        {{ region.nombre }}
      </ion-select-option>
    </ion-select>
  </ion-item>

  <!-- Botón para almacenar datos del usuario en Preferences -->
  <ion-button (click)="guardarDatosEnPreferences()">Guardar Datos del Usuario</ion-button>
</ion-content>
